sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
   var inputScope = inputs.flow_scope_sys_id;
  var newFlow = inputs.new_flow_sys_id;
  var group_name = inputs.group_name;
  var additional_message = inputs.wp_message;
  var workplaceConnection = inputs.workplace_connection;



  //update group_name
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '1', 'group_name', group_name); 

  //update group_name
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '2', 'additional_message', additional_message);

  new global.FlowTemplatesHelper().activateConnection(workplaceConnection);
})(inputs, outputs);

