sys_variable_value.value:
(function execute(inputs, outputs) {
  var inputScope = inputs.flow_scope_sys_id;
  var newFlow = inputs.new_flow_sys_id;
  var msTeamName = inputs.team_name;
  var channelName = inputs.channel_name;
  var title = inputs.title;
  var teamsConnection = inputs.teams_connection;

  // update MS teams name
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '1', 'ah_display_name1', msTeamName);

  // update MS Teams channel name
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '2', 'channel_name', channelName);

  // update MS Teams Title
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '5', 'title', title); 
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '8', 'title', title);
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '11', 'title', title);
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '13', 'title', title);

  //update active teams connection
  new global.FlowTemplatesHelper().activateConnection(teamsConnection);
})(inputs, outputs);

