sys_variable_value.value:
( function execute(inputs, outputs) {
// ... code ...

  var inputScope = inputs.flow_scope_sys_id;
  var newFlow = inputs.new_flow_sys_id;
  var slackChannel = inputs.slack_channel;
  var senderName = inputs.slack_user;
  var slackConnection = inputs.slack_connection;

  // update slack channel
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '3', 'ah_channel', slackChannel);
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '6', 'ah_channel', slackChannel);
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '9', 'ah_channel', slackChannel);
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '11', 'ah_channel', slackChannel);

  // update slack sender
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '3', 'ah_username', senderName);
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '6', 'ah_username', senderName);
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '9', 'ah_username', senderName);
  sn_flow.FlowTemplates.updateFlowActions(newFlow, '11', 'ah_username', senderName);
  //update slack connection
  new global.FlowTemplatesHelper().activateConnection(slackConnection);
})(inputs, outputs);

